var letreiro = document.querySelector('#letreiro');
var letreiroTexto = document.createElement('div');
letreiroTexto.id = 'letreiroTexto';
var mensagens = [
    "Valorizar Ideias",
    "Transparência",
    "Honestidade",
    "Proeficiencia",
    "Desempenho",
    "Inovação",
    "Valorizar pessoas"
];
letreiroTexto.innerText = mensagens.join('    ');
letreiro.appendChild(letreiroTexto);
// Adicionar estilos diretamente no TypeScript
letreiro.style.color = '#000';
letreiro.style.fontFamily = "'Open Sans', sans-serif";
letreiro.style.fontWeight = '600';
letreiro.style.fontSize = '22px';
letreiro.style.backgroundColor = '#fff';
letreiro.style.textAlign = 'center';
letreiro.style.width = '100%';
letreiro.style.overflow = 'hidden';
letreiroTexto.style.display = 'inline-block';
letreiroTexto.style.paddingLeft = '100%';
letreiroTexto.style.whiteSpace = 'nowrap';
letreiroTexto.style.animation = 'letreiro 15s linear infinite';
letreiroTexto.style.marginLeft = '50px'; // Espaçamento entre palavras
// Adicionar animação via JavaScript
var styleSheet = document.createElement("style");
styleSheet.type = "text/css";
styleSheet.innerText = "\n@keyframes letreiro {\n    0% { transform: translateX(0); }\n    100% { transform: translateX(-100%); }\n}\n";
document.head.appendChild(styleSheet);
var header = document.querySelector('header');
window.addEventListener('scroll', function () {
    if (window.scrollY === 0) {
        header.style.top = '0';
    }
    else {
        header.style.top = '-100px';
    }
});
var sections = document.querySelectorAll('section');
var isScrolling = false;
window.addEventListener('wheel', function (event) {
    if (isScrolling)
        return;
    isScrolling = true;
    var direction = event.deltaY > 0 ? 1 : -1;
    var currentSection = Math.round(window.scrollY / window.innerHeight);
    var nextSection = currentSection + direction;
    if (nextSection >= 0 && nextSection < sections.length) {
        window.scrollTo({
            top: sections[nextSection].offsetTop,
            behavior: 'smooth'
        });
    }
    setTimeout(function () {
        isScrolling = false;
    }, 1000);
});
