// Adiciona letreiro
const letreiro = document.createElement('div');
letreiro.id = 'letreiro';
letreiro.style.position = 'fixed';
letreiro.style.bottom = '0';
letreiro.style.width = '100%';
letreiro.style.backgroundColor = 'rgba(0, 0, 0, 0.7)';
letreiro.style.color = 'white';
letreiro.style.textAlign = 'center';
letreiro.style.padding = '10px 0';
letreiro.style.fontSize = '1.2em';
letreiro.style.whiteSpace = 'nowrap';
letreiro.style.overflow = 'hidden';
letreiro.style.zIndex = '1000'; // Aparece acima do footer

const letreiroTexto = document.createElement('div');
letreiroTexto.style.display = 'inline-block';
letreiroTexto.style.paddingLeft = '100%';
letreiroTexto.style.animation = 'letreiro 15s linear infinite';

const mensagens = [
    "Valorizar Ideias", 
    "Transparência", 
    "Honestidade", 
    "Proeficiencia", 
    "Desempenho", 
    "Inovação", 
    "Valorizar pessoas"
];

letreiroTexto.innerText = mensagens.join('    ');
letreiro.appendChild(letreiroTexto);
document.body.appendChild(letreiro);

const estiloAnimacao = document.createElement('style');
estiloAnimacao.innerHTML = `
@keyframes letreiro {
    0% { transform: translateX(0); }
    100% { transform: translateX(-100%); }
}
`;
document.head.appendChild(estiloAnimacao);

// Adiciona scroll suave por seção
const sections = document.querySelectorAll('section');
let isScrolling = false;

window.addEventListener('wheel', (event) => {
    if (isScrolling) return;
    
    isScrolling = true;
    const direction = event.deltaY > 0 ? 1 : -1;
    const currentSection = Math.round(window.scrollY / window.innerHeight);
    const nextSection = currentSection + direction;
    
    if (nextSection >= 0 && nextSection < sections.length) {
        window.scrollTo({
            top: sections[nextSection].offsetTop,
            behavior: 'smooth'
        });
    }

    setTimeout(() => {
        isScrolling = false;
    }, 1000);
});
